import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  city: string = 'Hanoi';
  currentWeather: any = null;
  loading = false;
  error: string | null = null;

  ngOnInit() {
    console.log('App initialized');
    this.testWeather();
  }

  testWeather() {
    this.loading = true;
    this.error = null;

    // Test data
    setTimeout(() => {
      this.currentWeather = {
        name: 'Hanoi',
        main: {
          temp: 25,
          feels_like: 27,
          humidity: 80,
          pressure: 1013
        },
        weather: [{
          description: 'mây rải rác',
          icon: '02d'
        }],
        wind: {
          speed: 5
        }
      };
      this.loading = false;
    }, 1000);
  }

  onCityChange() {
    console.log('City changed to:', this.city);
    this.testWeather();
  }

  getWeatherIcon(icon: string): string {
    return `https://openweathermap.org/img/wn/${icon}@2x.png`;
  }
}
<div class="weather-app">
  <header class="header">
    <h1>🌤️ Weather App</h1>
    <div class="search-container">
      <input 
        type="text" 
        [(ngModel)]="city" 
        (keyup.enter)="onCityChange()"
        placeholder="Nhập tên thành phố..."
        class="city-input"
      />
      <button (click)="onCityChange()" class="search-btn">Tìm kiếm</button>
    </div>
  </header>

  <main class="main-content">
    <div *ngIf="loading" class="loading">
      <div class="spinner"></div>
      <p>Đang tải dữ liệu thời tiết...</p>
    </div>

    <div *ngIf="error" class="error">
      <p>{{ error }}</p>
    </div>

    <!-- Current Weather -->
    <div *ngIf="currentWeather && !loading" class="current-weather">
      <div class="current-info">
        <h2>{{ currentWeather.name }}</h2>
        <div class="temperature">
          <span class="temp-value">{{ currentWeather.main.temp | number:'1.0-0' }}°C</span>
          <div class="weather-details">
            <img [src]="getWeatherIcon(currentWeather.weather[0].icon)" 
                 [alt]="currentWeather.weather[0].description"
                 class="weather-icon">
            <p class="weather-desc">{{ currentWeather.weather[0].description }}</p>
          </div>
        </div>
      </div>

      <div class="weather-stats">
        <div class="stat">
          <span class="stat-label">Cảm giác như</span>
          <span class="stat-value">{{ currentWeather.main.feels_like | number:'1.0-0' }}°C</span>
        </div>
        <div class="stat">
          <span class="stat-label">Độ ẩm</span>
          <span class="stat-value">{{ currentWeather.main.humidity }}%</span>
        </div>
        <div class="stat">
          <span class="stat-label">Áp suất</span>
          <span class="stat-value">{{ currentWeather.main.pressure }} hPa</span>
        </div>
        <div class="stat">
          <span class="stat-label">Tốc độ gió</span>
          <span class="stat-value">{{ currentWeather.wind.speed }} m/s</span>
        </div>
      </div>
    </div>

    <!-- Forecast -->
    <div *ngIf="forecast && !loading" class="forecast">
      <h3>Dự báo 5 ngày tới</h3>
      <div class="forecast-list">
        <div *ngFor="let item of getForecastForNextDays()" class="forecast-item">
          <div class="forecast-date">{{ formatDate(item.dt) }}</div>
          <div class="forecast-weather">
            <img [src]="getWeatherIcon(item.weather[0].icon)" 
                 [alt]="item.weather[0].description"
                 class="forecast-icon">
            <span class="forecast-desc">{{ item.weather[0].description }}</span>
          </div>
          <div class="forecast-temps">
            <span class="temp-max">{{ item.main.temp_max | number:'1.0-0' }}°</span>
            <span class="temp-min">{{ item.main.temp_min | number:'1.0-0' }}°</span>
          </div>
        </div>
      </div>
    </div>
  </main>
</div>
