import { Component, OnInit } from '@angular/core';
import { WeatherService } from './services/weather.service';
import { CurrentWeather, Forecast, ForecastItem } from './models/weather.model';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  currentWeather: CurrentWeather | null = null;
  forecast: Forecast | null = null;
  city: string = '';
  loading = false;
  error: string | null = null;
  locationPermission = false;
  currentLocation: { lat: number; lon: number } | null = null;

  constructor(private weatherService: WeatherService) {}

  ngOnInit() {
    this.getCurrentLocationWeather();
  }

  getCurrentLocationWeather() {
    this.loading = true;
    this.error = null;

    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          this.currentLocation = {
            lat: position.coords.latitude,
            lon: position.coords.longitude
          };
          this.locationPermission = true;
          this.loadWeatherByCoords();
        },
        (error) => {
          console.error('Geolocation error:', error);
          this.error = 'Không thể lấy vị trí hiện tại. Vui lòng nhập tên thành phố.';
          this.loading = false;
          this.loadDefaultWeather();
        }
      );
    } else {
      this.error = 'Trình duyệt không hỗ trợ định vị. Vui lòng nhập tên thành phố.';
      this.loading = false;
      this.loadDefaultWeather();
    }
  }

  loadWeatherByCoords() {
    if (!this.currentLocation) return;

    this.weatherService.getCurrentWeatherByCoords(this.currentLocation.lat, this.currentLocation.lon).subscribe({
      next: (data) => {
        this.currentWeather = data;
        this.city = data.name;
        this.loading = false;
      },
      error: (error) => {
        this.error = 'Không thể tải dữ liệu thời tiết.';
        this.loading = false;
        console.error('Error fetching current weather:', error);
      }
    });

    this.weatherService.getForecastByCoords(this.currentLocation.lat, this.currentLocation.lon).subscribe({
      next: (data) => {
        this.forecast = data;
      },
      error: (error) => {
        console.error('Error fetching forecast:', error);
      }
    });
  }

  loadDefaultWeather() {
    this.city = 'Hanoi';
    this.getWeather();
  }

  getWeather() {
    if (!this.city.trim()) return;

    this.loading = true;
    this.error = null;

    this.weatherService.getCurrentWeather(this.city).subscribe({
      next: (data) => {
        this.currentWeather = data;
        this.loading = false;
      },
      error: (error) => {
        this.error = 'Không thể tải dữ liệu thời tiết. Vui lòng kiểm tra tên thành phố.';
        this.loading = false;
        console.error('Error fetching current weather:', error);
      }
    });

    this.weatherService.getForecast(this.city).subscribe({
      next: (data) => {
        this.forecast = data;
      },
      error: (error) => {
        console.error('Error fetching forecast:', error);
      }
    });
  }

  onCityChange() {
    if (this.city.trim()) {
      this.getWeather();
    }
  }

  onLocationClick() {
    this.getCurrentLocationWeather();
  }

  getWeatherIcon(icon: string): string {
    return `https://openweathermap.org/img/wn/${icon}@2x.png`;
  }

  getForecastForNextDays(): ForecastItem[] {
    if (!this.forecast) return [];

    const today = new Date();
    const nextDays = [];

    for (let i = 1; i <= 5; i++) {
      const targetDate = new Date(today);
      targetDate.setDate(today.getDate() + i);

      const dayForecasts = this.forecast.list.filter(item => {
        const itemDate = new Date(item.dt * 1000);
        return itemDate.getDate() === targetDate.getDate() && 
               itemDate.getMonth() === targetDate.getMonth();
      });

      if (dayForecasts.length > 0) {
        nextDays.push(dayForecasts[0]);
      }
    }

    return nextDays;
  }

  formatDate(timestamp: number): string {
    const date = new Date(timestamp * 1000);
    return date.toLocaleDateString('vi-VN', { 
      weekday: 'long', 
      day: 'numeric', 
      month: 'long' 
    });
  }

  getTemperatureColor(temp: number): string {
    if (temp < 0) return '#4A90E2'; // Blue for very cold
    if (temp < 10) return '#7ED321'; // Light blue for cold
    if (temp < 20) return '#F5A623'; // Yellow for cool
    if (temp < 30) return '#FF8C00'; // Orange for warm
    return '#D0021B'; // Red for hot
  }

  getWindDirection(deg: number): string {
    const directions = ['Bắc', 'Đông Bắc', 'Đông', 'Đông Nam', 'Nam', 'Tây Nam', 'Tây', 'Tây Bắc'];
    const index = Math.round(deg / 45) % 8;
    return directions[index];
  }
}
<div class="weather-app">
  <header class="header">
    <div class="header-content">
      <h1>🌤️ Weather App</h1>
      <p class="subtitle">Thời tiết chính xác, cập nhật liên tục</p>
    </div>

    <div class="search-container">
      <div class="search-box">
        <input 
          type="text" 
          [(ngModel)]="city" 
          (keyup.enter)="onCityChange()"
          placeholder="Tìm kiếm thành phố..."
          class="city-input"
        />
        <button (click)="onCityChange()" class="search-btn">
          <span class="search-icon">🔍</span>
        </button>
      </div>

      <button (click)="onLocationClick()" class="location-btn" [disabled]="loading">
        <span class="location-icon">📍</span>
        <span>Vị trí hiện tại</span>
      </button>
    </div>
  </header>

  <main class="main-content">
    <!-- Loading State -->
    <div *ngIf="loading" class="loading-container">
      <div class="loading-spinner">
        <div class="spinner"></div>
        <div class="spinner"></div>
        <div class="spinner"></div>
      </div>
      <p class="loading-text">Đang tải dữ liệu thời tiết...</p>
    </div>

    <!-- Error State -->
    <div *ngIf="error && !loading" class="error-container">
      <div class="error-icon">⚠️</div>
      <p class="error-text">{{ error }}</p>
      <button (click)="getCurrentLocationWeather()" class="retry-btn">
        Thử lại
      </button>
    </div>

    <!-- Current Weather -->
    <div *ngIf="currentWeather && !loading" class="current-weather-card">
      <div class="weather-header">
        <div class="location-info">
          <h2 class="city-name">{{ currentWeather.name }}</h2>
          <p class="location-type" *ngIf="locationPermission">📍 Vị trí hiện tại</p>
          <p class="location-type" *ngIf="!locationPermission">🔍 Tìm kiếm</p>
        </div>
        <div class="weather-icon-large">
          <img [src]="getWeatherIcon(currentWeather.weather[0].icon)" 
               [alt]="currentWeather.weather[0].description"
               class="weather-icon">
        </div>
      </div>

      <div class="temperature-section">
        <div class="main-temp" [style.color]="getTemperatureColor(currentWeather.main.temp)">
          {{ currentWeather.main.temp | number:'1.0-0' }}°C
        </div>
        <div class="weather-description">
          {{ currentWeather.weather[0].description }}
        </div>
        <div class="feels-like">
          Cảm giác như {{ currentWeather.main.feels_like | number:'1.0-0' }}°C
        </div>
      </div>

      <div class="weather-details-grid">
        <div class="detail-item">
          <div class="detail-icon">💧</div>
          <div class="detail-content">
            <span class="detail-label">Độ ẩm</span>
            <span class="detail-value">{{ currentWeather.main.humidity }}%</span>
          </div>
        </div>

        <div class="detail-item">
          <div class="detail-icon">🌡️</div>
          <div class="detail-content">
            <span class="detail-label">Áp suất</span>
            <span class="detail-value">{{ currentWeather.main.pressure }} hPa</span>
          </div>
        </div>

        <div class="detail-item">
          <div class="detail-icon">💨</div>
          <div class="detail-content">
            <span class="detail-label">Gió</span>
            <span class="detail-value">{{ currentWeather.wind.speed }} m/s {{ getWindDirection(currentWeather.wind.deg) }}</span>
          </div>
        </div>

        <div class="detail-item">
          <div class="detail-icon">👁️</div>
          <div class="detail-content">
            <span class="detail-label">Tầm nhìn</span>
            <span class="detail-value">{{ (currentWeather.visibility / 1000) | number:'1.1-1' }} km</span>
          </div>
        </div>
      </div>
    </div>

    <!-- Forecast -->
    <div *ngIf="forecast && !loading" class="forecast-section">
      <h3 class="forecast-title">📅 Dự báo 5 ngày tới</h3>
      <div class="forecast-grid">
        <div *ngFor="let item of getForecastForNextDays(); let i = index" 
             class="forecast-card" 
             [style.animation-delay]="(i * 0.1) + 's'">
          <div class="forecast-date">{{ formatDate(item.dt) }}</div>
          <div class="forecast-weather">
            <img [src]="getWeatherIcon(item.weather[0].icon)" 
                 [alt]="item.weather[0].description"
                 class="forecast-icon">
            <span class="forecast-desc">{{ item.weather[0].description }}</span>
          </div>
          <div class="forecast-temps">
            <span class="temp-max">{{ item.main.temp_max | number:'1.0-0' }}°</span>
            <span class="temp-min">{{ item.main.temp_min | number:'1.0-0' }}°</span>
          </div>
          <div class="forecast-details">
            <div class="forecast-humidity">💧 {{ item.main.humidity }}%</div>
            <div class="forecast-wind">💨 {{ item.wind.speed }} m/s</div>
          </div>
        </div>
      </div>
    </div>
  </main>

  <footer class="footer">
    <p>Dữ liệu từ OpenWeatherMap</p>
  </footer>
</div>
